/**
 * v12.1: Ð†Ð½Ñ–Ñ†Ñ–Ð°Ð»Ñ–Ð·Ð°Ñ†Ñ–Ñ dark mode.
 * ÐŸÑ€Ñ–Ð¾Ñ€Ð¸Ñ‚ÐµÑ‚: localStorage > Ð°Ð²Ñ‚Ð¾Ð¼Ð°Ñ‚Ð¸Ñ‡Ð½Ð¾ Ð·Ð° Ñ‡Ð°ÑÐ¾Ð¼ (20:00â€“7:00 = Ñ‚ÐµÐ¼Ð½Ð°).
 * Ð—Ð°ÑÑ‚Ð¾ÑÐ¾Ð²ÑƒÑ” body.dark-mode + data-theme="dark" Ð´Ð»Ñ Ð¿Ð¾Ð²Ð½Ð¾Ñ— ÑÑƒÐ¼Ñ–ÑÐ½Ð¾ÑÑ‚Ñ–.
 */
function initDarkMode() {
    const saved = localStorage.getItem('pzp_dark_mode');
    let isDark;
    if (saved === 'true') {
        isDark = true;
    } else if (saved === 'false') {
        isDark = false;
    } else {
        // ÐÐ²Ñ‚Ð¾: Ñ‚ÐµÐ¼Ð½Ð° Ñ‚ÐµÐ¼Ð° Ð· 20:00 Ð´Ð¾ 07:00
        const hour = new Date().getHours();
        isDark = hour >= 20 || hour < 7;
    }
    document.body.classList.toggle('dark-mode', isDark);
    document.documentElement.setAttribute('data-theme', isDark ? 'dark' : 'light');
    return isDark;
}

